<?php$role = $this->session->userdata('permission');if (!isset($role) || $role < 2) {    redirect(site_url() . '/order/login');}/* Clear order session data in case the manager modified an order. */$this->session->unset_userdata('order_id');$this->session->unset_userdata('phone');$this->session->unset_userdata('current_item_id');$this->session->unset_userdata('current_item_name');$this->session->unset_userdata('std_toppings');date_default_timezone_set($this->config->item('timezone'));?><script type="text/javascript">            $(document).ready(    function () {	setTimeout('location.href = "<?php echo site_url('manager/dashboard'); ?>"', <?php echo $refresh_rate; ?>)    });</script><div class="ui-bar ui-bar-e" style="text-align:center;"><h4 >The Dashboard  [<?php echo $location_name . ':' . $this->session->userdata('location'); ?>]</h4></div><br/><a href="<?php echo site_url("order/quick_order"); ?>" <?php echo $this->config->config['link_options_basic']; ?>>Place Order</a><div data-role="controlgroup">    <?php if ($role > 1) echo '<a href="' . site_url("manager/user_manager") . '" ' . $this->config->config['link_options_basic'] . '>User Manager</a>'; ?>    <?php if ($role > 2) echo '<a href="' . site_url("manager/menu_manager") . '" ' . $this->config->config['link_options_basic'] . '>Menu Manager</a>'; ?>    <?php if ($role > 3) echo '<a href="' . site_url("manager/edit_hours") . '?table=pickup' . '" ' . $this->config->config['link_options_basic'] . '>Location Hours</a>'; ?>    <?php if ($role > 3) echo '<a href="' . site_url("manager/edit_hours") . '?table=delivery' . '" ' . $this->config->config['link_options_basic'] . '>Delivery Hours</a>'; ?>    <?php if ($role > 3) echo '<a href="' . site_url("manager/reports").'" ' . $this->config->config['link_options_basic'] . '>Reports</a>'; ?>    <?php if ($role > 3) echo '<a href="' . site_url("manager/promos").'" ' . $this->config->config['link_options_basic'] . '>Promotions</a>'; ?>    <?php if ($role > 3) echo '<a href="' . site_url("manager/marketing").'" ' . $this->config->config['link_options_basic'] . '>Marketing</a>'; ?></div><a href="<?php echo site_url("manager/logout"); ?>" <?php echo $this->config->config['link_options_basic']; ?>>Logout</a><br/><a id="Incoming" href="#" onclick="toggle('Incoming');" <?php echo $this->config->config['link_div_control']; ?>>Incoming Orders</a><div id="IncomingDiv" style="display: block;">    <table>	<tr><th>TimeIn</th><th>ScheduleOut</th><th>Order #</th><th>Phone Number</th><th>Bill</th><th>Deliver</th></tr>	<?php	$orders = $order_data['orders'];	$items = $order_data['items'];	$toppings = $order_data['toppings'];	$total = 0.00;	if (isset($orders)) {	    foreach ($orders->result() as $order) {		$order_id = $order->id;		$phone = $order->phone;		$submitted = $order->submitted;		$verified = $order->verified;		$delivery = $order->delivery;		$icon = 'no.png';		if ($delivery)		    $icon = 'yes.png';		$d_time_raw = $order->delivery_time;		$d_time = date('M d g:iA', $d_time_raw);		if ($verified)		    continue; //jump out if verified		if ($d_time_raw > (time() + $lead_time))		    continue; //if the order is not within the lead time do not show		foreach ($items->result() as $item) {		    if ($item->order_id == $order_id) {			$item_price = $item->price;			$item_id = $item->id;			$total = $total + $item_price;			foreach ($toppings->result() as $topping) {			    $topping_id = $topping->item_id;			    if ($item_id == $topping_id) {				$price = $topping->price;				$total = $total + $price;			    }			}		    }		}		echo '<tr><td>' . date('M d g:iA', $submitted) . '</td><td>' . $d_time . '</td><td><a href="' . site_url("order/view_order") . '/' . $order_id . '" ' . $this->config->config['table_edit_basic'] . ' rel="external">' . $order_id . '</a></td><td style="color: blue;">' . $phone . '</td><td style="color: green;">$' . $total . '</td><td><img src="' . base_url() . 'images/' . $icon . '"/></td></tr>';		$total = 0.00;	    }	}	echo '</table>';	?></div><br/><a id="Verified" href="#" onclick="toggle('Verified');" <?php echo $this->config->config['link_div_control']; ?>>Verified Orders</a><div id="VerifiedDiv" style="display: block;">    <table>	<tr><th>TimeIn</th><th>ScheduleOut</th><th>Order #</th><th>Phone Number</th><th>Bill</th><th>Deliver</th></tr>	<?php	if (isset($orders)) {	    foreach ($orders->result() as $order) {		$order_id = $order->id;		$phone = $order->phone;		$submitted = $order->submitted;		$verified = $order->verified;		$ready = $order->ready_done;		if (!$verified || $ready)		    continue; //jump out if not verified		if ($d_time_raw > (time() + $lead_time))		    continue;		$delivery = $order->delivery;		$icon = 'no.png';		if ($delivery)		    $icon = 'yes.png';		$d_time = $order->delivery_time;		if ($d_time < time()) {		    $d_time = 'ASAP';		}else		    $d_time = date('M d g:iA', $d_time);		foreach ($items->result() as $item) {		    if ($item->order_id == $order_id) {			$item_price = $item->price;			$item_id = $item->id;			$total = $total + $item_price;			foreach ($toppings->result() as $topping) {			    $topping_id = $topping->item_id;			    if ($item_id == $topping_id) {				$price = $topping->price;				$total = $total + $price;			    }			}		    }		}		echo '<tr><td>' . date('M d g:iA', $submitted) . '</td><td>' . $d_time . '</td><td><a href="' . site_url("order/view_order") . '/' . $order_id . '" ' . $this->config->config['table_edit_basic'] . ' rel="external">' . $order_id . '</a></td><td style="color: blue;">' . $phone . '</td><td style="color: green;">$' . $total . '</td><td><img src="' . base_url() . 'images/' . $icon . '"/></td></tr>';		$total = 0.00;	    }	}	echo '</table>';	?></div><br/><a id="Completed" href="#" onclick="toggle('Completed');" <?php echo $this->config->config['link_div_control']; ?>>Completed Orders</a><div id="CompletedDiv" style="display: block;"