<?php$role = $this->session->userdata('permission');if (!isset($role) || $role < 3) {    redirect(site_url() . '/order/login');}if (!isset($result)) {    $id = 0;    $name = "";    $desc = "";    $price = "";    $active = 1;    if (isset($category)) {	$cat = $category;    }else	$cat = "";    $table = "";    $tprice = 0;    $show_price = 0;    $combine = 0;}else {    $item = $result['item']->row();    $id = $item->id;    $name = $item->item_name;    $desc = $item->description;    $price = $item->price;    $active = $item->enabled;    $cat = $item->category;    $table = $item->toppings_table;    $tprice = $item->topping_price;    $show_price = $item->show_top_price;    $combine = $item->combo_top_prices;    $std_toppings = $result['std_toppings'];    //cleanup after adding an included topping    $this->session->unset_userdata('current_item_name');    $this->session->unset_userdata('std_toppings');}?><script type="text/javascript">    function formSaveAction()    {	//change the form action	document.getElementById('editItemForm').action = "<?php echo site_url('manager/update_menu_item/2'); ?>";	//document.forms['editItemForm'].action = "<?php echo site_url('manager/update_menu_item/2'); ?>";	//submit form	document.getElementById('editItemForm').submit();	//document.forms['editItemForm'].submit();    }</script><div class="ui-bar ui-bar-e" style="text-align:center;"><h4 >Edit Menu Item</h4></div><br/><form id="editItemForm" action="<?php echo site_url('manager/update_menu_item'); ?>" method="post" accept-charset="utf-8">    <input type="hidden" name="id"  value="<?php echo $id; ?>"/>    <div data-role="fieldcontain">	<label for="item_name">Item Name</label>	<input type="text" id="item_name" name="item_name"  value="<?php echo $name; ?>" autofocus />    </div>    <div data-role="fieldcontain">	<label for="desc">Description</label>	<textarea name="desc" id="desc" rows="4" cols="40"><?php echo $desc; ?></textarea>    </div>    <div data-role="fieldcontain">	<label for="price">Price</label>	<input type="text" id="price" name="price" value="<?php echo $price; ?>"/>    </div>    <div data-role="fieldcontain">	<label for="cat">Menu Category</label>	<input type="text" id="cat" name="cat" value="<?php echo $cat; ?>"/>    </div>	    <div data-role="fieldcontain">	<label for="toppingsTable">Toppings Group</label>	<select name="table" id="toppingsTable" >	    <option value="none" <?php if ($table == "none") echo 'selected'; ?>>No Toppings</option>	    <option value="menu_sand_toppings" <?php if ($table == "menu_sand_toppings") echo 'selected'; ?>>Sandwich Toppings</option>	    <option value="menu_pizza_toppings" <?php if ($table == "menu_pizza_toppings") echo 'selected'; ?>>Pizza Toppings</option>	</select>    </div>    <a id="stdTopping" href="#" onclick="toggle('stdTopping');"; <?php echo $this->config->config['link_div_control']; ?>>Included Toppings</a>    <div id="stdToppingDiv" style="display: block;">	<table>	    <tr><th><a href="<?php echo site_url("manager/add_std_topping") . '?id=' . $id . '&cat=' . $cat . '&item_name=' . $name . '&table=' . $table; ?>" onClick="formSaveAction()" data-rel="dialog" data-transition="pop" <?php echo $this->config->config['link_mini_icon']; ?> data-icon="plus"></a></th><th>Toppings</th></tr>	    <?php//set session var ['std_toppings']=$item_id, save this form first, then go to the add_topping track	    if (isset($std_toppings)) {		foreach ($std_toppings->result() as $topping) {		    $top_id = $topping->id;		    $top_name = $topping->topping_name;		    echo '<tr><td><a href="' . site_url("manager/del_std_topping") . '?id=' . $top_id . '&item_id=' . $id . '" data-rel="dialog" data-transition="pop" ' . $this->config->config['link_mini_icon'] . ' data-icon="delete"></td><td>' . $top_name . '</td></tr>';		}	    }	    ?>	</table>    </div>    <br/>    <div data-role="fieldcontain">				<label for="tprice">Topping Price (This Item)</label>	<input type="text" name="tprice" id="tprice"  value="<?php echo $tprice; ?>" />    </div>    <div data-role="fieldcontain">	<fieldset data-role="controlgroup">	    <label for="show_price">Show Topping Price (Doesn't include combined price.)</label>	    <?php	    if ($show_price != 0) {		echo '<input type="checkbox" id="show_price" name="show_price" value="1" checked="true"/>';	    }else		echo '<input type="checkbox" id="show_price" name="show_price" value="1"/>';	    ?>								    <label for="combine">Combine Topping Prices</label>	    <?php	    if ($combine != 0) {		echo '<input id="combine" type="checkbox" name="combine" value="1" checked="true"/>';	    }else		echo '<input id="combine" type="checkbox" name="combine" value="1"/>';	    ?>		    <label for="active">Active</label>	    <?php	    if ($active != 0) {		echo '<input type="checkbox" id="active" name="active" value="1" class="custom" checked="true"/>';	    }else		echo '<input type="checkbox" id="active" name="active" class="custom" value="1"/>';	    ?>		</fieldset>    </div>    <div class="ui-body ui-body-b">	<fieldset class="ui-grid-a">			    <input type="submit" <?php echo $this->config->config['submit_button_basic']; ?> name="submit" value="Save"/>								</fieldset>    </div>    <br/></form>	 			<?phpecho validation_errors('<p class="error">');if (isset($problem)) {    echo '<p class="error">' . $problem . '</p>';}?><div data-role="controlgroup"><?php if ($role > 2) echo '<a href="' . site_url("manager/menu_manager") . '"' . $this->config->config['link_options_back'] . '>Menu Manager</a>'; ?>    <a href="<?php echo site_url("manager/dashboard"); ?>"<?php echo $this->config->config['link_options_basic']; ?>>Dashboard</a>    <a href="<?php echo site_url("manager/delete_menu_item") . '?id=' . $id; ?>"<?php echo $this->config->config['link_options_basic']; ?>>Delete This Item</a></div>