<?php$role = $this->session->userdata('permission');if (!isset($role) || $role < 2) {    redirect(site_url() . '/order/login');}$this->session->unset_userdata('current_item_name');$this->session->unset_userdata('std_toppings');/*  date_default_timezone_set($this->config->item('timezone')); */?><div class="ui-bar ui-bar-e" style="text-align:center;"><h4 >Menu Manger</h4></div><br/><a href="<?php echo site_url("manager/dashboard"); ?>"<?php echo $this->config->config['link_options_back']; ?>>Dashboard</a><div data-role="controlgroup">    <a href="<?php echo site_url("manager/manage_toppings") . '?table=menu_pizza_toppings'; ?>"<?php echo $this->config->config['link_options_basic']; ?>>Pizza Toppings</a>    <a href="<?php echo site_url("manager/manage_toppings") . '?table=menu_sand_toppings'; ?>"<?php echo $this->config->config['link_options_basic']; ?>>Sandwich Toppings</a></div><a href="<?php echo site_url("manager/change_menu_item") . '?id=0'; ?>"<?php echo $this->config->config['link_menu_add']; ?>>Add Menu Item</a><?phpif (isset($menu)) {    $current = "";    foreach ($menu->result() as $item) {	$id = $item->id;	$name = $item->item_name;	$desc = $item->description;	$price = $item->price;	$category = $item->category;	$enabled = $item->enabled;	if ($enabled == 1) {	    $enabled = 'Y';	    $g = 'yes.png';	} else {	    $enabled = 'N';	    $g = 'no.png';	}	$color = "green";	if ($mod_id == $id)	    $color = "red";	if ($current != $category) {	    if ($current != "") {		echo '</table>';		echo '</div>';	    }	    echo '<br/>';	    echo '<a id="' . $category . '"#" onclick="toggle(' . '\'' . $category . '\'' . ');" data-role="button" data-icon="minus" data-mini="true" data-inline="true">' . ucfirst($category) . '</a>';	    echo '<div id="' . $category . 'Div" style="display: block;">';	    echo '<table>';	    echo '<tr><th><a href="' . site_url("manager/change_menu_item") . '?id=0&cat=' . $category . '"' . $this->config->config['table_add_basic'] . '>Item</a></th><th>Description</th><th>Price</th><th>Active</th></tr>';	    $current = $category;	}	echo '<tr><td style="color:' . $color . ';"><a href="' . site_url("manager/change_menu_item") . '?id=' . $id . '"' . $this->config->config['table_edit_basic'] . '>' . $name . '</a></td><td style="width: 40%;">' . substr($desc, 0, 45) . '...</td><td>' . $price . '</td><td><img src="' . base_url() . 'images/' . $g . '"/></td></tr>';    }    echo '</table>';}?>