<?php$role = $this->session->userdata('permission');if (!isset($role) || $role < 3) {    redirect('manager/login_bounce');}if (!isset($record)) {    $id = 0;    $name = "";    $price = "";    $cat = "";    if (isset($category))	$cat = $category;    $active = 1;}else {    $topping = $record->row();    $id = $topping->id;    $name = $topping->topping_name;    $price = $topping->price;    $cat = $topping->category;    $active = $topping->enabled;}if ($table == "menu_pizza_toppings") {    $title = "Pizza";}else    $title = "Sandwich";?><div class="ui-bar ui-bar-e" style="text-align:center;"><h4 >Edit <?php echo $title; ?> Topping</h4></div><br/><form id="editToppingForm" action="<?php echo site_url('manager/update_topping'); ?>" method="post" accept-charset="utf-8">     <div class="legend">Edit <?php echo $title; ?> Topping</div>    <div data-role="fieldcontain">	<input type="hidden" name="id" id="id"  value="<?php echo $id; ?>"/>    </div>		    <div data-role="fieldcontain">	<input type="hidden" name="table" id="table" value="<?php echo $table; ?>"/>    </div>    <div data-role="fieldcontain">	<label for="name">Topping Name</label>	<input type="text" name="name" id="name" value="<?php echo $name; ?>" autofocus />    </div>    <div data-role="fieldcontain">	<label for="price">Price</label>	<input type="text" name="price" id="price" value="<?php echo $price; ?>"/>    </div>    <div data-role="fieldcontain">	<label for="cat">category</label>	<input type="text" name="cat" id="cat" value="<?php echo $cat; ?>"/>    </div>    <div data-role="fieldcontain">	<label for="active">Active</label>	<?php	if ($active != 0) {	    echo '<input type="checkbox" id="active" name="active" value="1" checked="true"/>';	}else	    echo '<input type="checkbox" id="active" name="active" value="1"/>';	?>				    </div>    <div class="ui-body ui-body-b">	<fieldset class="ui-grid-a">			    <input type="submit" <?php echo $this->config->config['submit_button_basic']; ?> name="submit" value="Save"/>								</fieldset>    </div>    <br/></form><?php//ajax this?echo validation_errors('<p class="error">');if (isset($problem)) {    echo '<p class="error">' . $problem . '</p>';}?><div data-role="controlgroup">    <a href="<?php echo site_url("manager/manage_toppings") . '?table=' . $table; ?>"<?php echo $this->config->config['link_options_basic']; ?>><?php echo $title; ?> Toppings</a><?php if ($role > 2) echo '<a href="' . site_url("manager/menu_manager") . '"' . $this->config->config['link_options_basic'] . '>Menu Manager</a>'; ?>    <a href="<?php echo site_url("manager/dashboard"); ?>"<?php echo $this->config->config['link_options_basic']; ?>>Dashboard</a>    <a href="<?php echo site_url("manager/delete_topping") . '?id=' . $id; ?>"<?php echo $this->config->config['link_options_basic']; ?>>Delete This Topping</a></div>