<?php$role = $this->session->userdata('permission');if (!isset($role) || $role < 2) {    redirect(site_url() . '/order/login');}/*  date_default_timezone_set($this->config->item('timezone')); */?><div class="ui-bar ui-bar-e" style="text-align:center;"><h4 >User Manger</h4></div><br/><a href="<?php echo site_url("manager/dashboard"); ?>"<?php echo $this->config->config['link_options_back']; ?>>Dashboard</a><a href="<?php echo site_url("manager/add_user"); ?>"<?php echo $this->config->config['link_menu_add']; ?>>Add User</a><br/><a id="staff" href="#" onclick="toggle('staff');"; <?php echo $this->config->config['link_div_control']; ?>>Staff</a><div id="staffDiv" style="display: block;">    <table>	<?php	if (isset($managers)) {	    echo '<tr><th>Email</th><th>Name</th><th>Phone</th><th>Group</th><th>Active</th></tr>';	    foreach ($managers->result() as $rec) {		$id = $rec->id;		$email = $rec->email;		$name = $rec->first_name;		$phone = $rec->phone;		$active = $rec->active;		$group = $rec->group_id;		switch ($group) {		    case 1:			$group = 'Customer';			break;		    case 2:			$group = 'Cook';			break;		    case 3:			$group = 'Driver';			break;		    case 4:			$group = 'Supervisor';			break;		    case 5:			$group = 'Manager';			break;		    case 6:			$group = 'Owner';					}		if ($active) {		    $active = 'yes.png';		} else {		    $active = 'no.png';		}		echo '<tr><td><a href="' . site_url("manager/change_user") . '?id=' . $id . '"' . $this->config->config['table_edit_basic'] . '>' . $email . '</a></td><td>' . $name . '</td><td>' . $phone . '</td><td>' . $group . '</td><td><img src="' . base_url() . 'images/' . $active . '"/></td></tr>';	    }	    echo '</table>';	}	?></div><br/><a id="customers" href="#" onclick="toggle('customers');"; <?php echo $this->config->config['link_div_control']; ?>>Customers</a><div id="customersDiv" style="display: block;">    <table>	<?php	if (isset($managers)) {	    echo '<tr><th>Email</th><th>Name</th><th>Phone</th><th>Active</th></tr>';	    foreach ($customers->result() as $rec) {		$id = $rec->id;		$email = $rec->email;		$name = $rec->first_name;		$phone = $rec->phone;		$active = $rec->active;		if ($active) {		    $active = 'yes.png';		} else {		    $active = 'no.png';		}		echo '<tr><td><a href="' . site_url("manager/change_user") . '?id=' . $id . '"' . $this->config->config['table_edit_basic'] . '>' . $email . '</a></td><td>' . $name . '</td><td>' . $phone . '</td><td><img src="' . base_url() . 'images/' . $active . '"/></td></tr>';	    }	    echo '</table>';	}	?></div>