<?php$order_id = $this->session->userdata('order_id');$user_id = $this->session->userdata('user_id');$role = $this->session->userdata('permission');if (!isset($role) || $role < 1) {    redirect(site_url('/order/login'));}$email = $this->session->userdata('email_address');date_default_timezone_set($this->config->item('timezone'));if (!isset($delivery))    $delivery = FALSE;if ($delivery) {    $type = "DELIVERY";}else    $type = "PICKUP";$order_row = $order->row();if (!isset($order_id) || $order_id == "") {    $order_id = $order_row->id;    $this->session->set_userdata('order_id', $order_id);    $this->session->set_userdata('phone', $order_row->phone);    $this->session->set_userdata('name', $order_row->name);    $this->session->set_userdata('carrier', $order_row->carrier);}$submitted = $order_row->submitted;$verified = $order_row->verified;$phone = $order_row->phone;$ready = $order_row->ready_done;$delivery = $order_row->delivery;$d_time = $order_row->delivery_time;echo '<<<<<<<  MyPizzeria >>>>>>><br/><br/>';if ($delivery) {    echo ' * DELIVERY *<br/>';}else    echo ' - PICKUP -<br/>';echo 'Order ID:  ' . $order_id . '<br/>';echo 'TimeIn:  ' . date('M d g:iA', $submitted) . '<br/>';echo 'ScheduleOut:  ' . date('M d g:iA', $d_time) . '<br/>';echo'<br/><br/>';?><table>    <?php    $total = 0.00;    if (isset($items)) {	echo '<tr style="text-align: left;"><th>Item/Topping</th><th>Half</th><th>Price</th></tr>';	foreach ($items->result() as $item) {	    $name = $item->name;	    $item_price = $item->price;	    $item_id = $item->id;	    echo '<tr><td>' . $name . '</td><td></td><td>' . $item_price . '</td></tr>';	    $total = $total + $item_price;	    foreach ($toppings->result() as $topping) {		$id = $topping->item_id;		$topping_id = $topping->id;		if ($item_id == $id) {		    $inst = $topping->comment;		    $price = $topping->price;		    $half = $topping->half;		    if ($half == 'W' || $half == 'X')			$half = "";		    if ($half == 'L')			$half = "Left";		    if ($half == 'R')			$half = "Right";		    echo '<tr><td>  -  ' . $inst . '</td><td>' . $half . '</td><td>' . $price . '</td></tr>';		    $total = $total + $price;		}	    }	}	echo '<tr><td><strong>' . $type . ' TOTAL</strong></td><td></td><td><strong>$' . $total . '</strong></td></tr>';    }    ?></table><br/>