<?php$order_id = $this->session->userdata('order_id');if (!isset($order_id))    redirect(site_url('/order/login/'));$role = $this->session->userdata('permission');if (!isset($delivery))    $delivery = 0;?><img style="position:absolute; left:25px; top:5px;" src="<?php base_url(); ?>/images/mario.64.png" /><form id="deliveryAddressForm" action="<?php echo site_url("order/save_delivery_address"); ?>" method="post" accept-charset="utf-8">    <div class="ui-bar ui-bar-e"><h2>Delivery Address</h2></div> 				<?php if (isset($problem)) echo '<p>' . $problem . '</p>'; //not really a "problem"?>      <div data-role="fieldcontain">		<label for="delivery">Delivery</label>	<?php	if ($delivery != 0) {	    echo '<input type="checkbox" id="delivery" name="delivery" value="1" class="custom" checked="true"/>';	}else	    echo '<input type="checkbox" id="delivery" name="delivery" class="custom" value="1"/>';	?>	    </div>    <?php    if ($delivery) {	echo '<p style="color: green; text-align: center;">Order is marked for delivery!</p>';    } else {	echo '<p style="color: red; text-align: center;">Order is NOT marked for delivery.</p>';    }    ?>    <div data-role="fieldcontain">		<label for="street">Street Address</label>	<input type="text" name="street" id="street" value="<?php if (isset($street)) echo $street; ?>" autofocus/>    </div>    <div data-role="fieldcontain">		<label for="addr2">Alt Address (Apt #)</label>	<input type="text" name="addr2" id="addr2" value="<?php if (isset($addr2)) echo $addr2; ?>"/>    </div>    <div data-role="fieldcontain">		<label for="city">City</label>	<input type="text" name="city" id="city" value="<?php if (isset($city)) echo $city; ?>"/>    </div>    <div data-role="fieldcontain">		<label for="state">State</label>	<input type="text" name="state" id="state" value="<?php if (isset($state)) echo $state; ?>"/>    </div>    <div data-role="fieldcontain">		<label for="zip">Zip Code</label>	<input type="text" name="zip" id="zip" value="<?php if (isset($zip)) echo $zip; ?>"/>    </div>    <div class="ui-body ui-body-b">	<fieldset class="ui-grid-a">			    <input type="submit" <?php echo $this->config->config['submit_button_basic']; ?> name="submit" value="Save"/>	</fieldset>    </div></form><br/><div data-role="controlgroup">    <a href="<?php echo site_url("order/order_top"); ?>" <?php echo $this->config->config['link_options_basic']; ?> >Go To Menu</a>    <a href="<?php echo site_url("order/view_order") . '?id=' . $order_id; ?>" <?php echo $this->config->config['link_options_basic']; ?> >View Order</a>			<?php if ($role > 1) echo '<a href="' . site_url("manager/dashboard") . '"' . $this->config->config['link_options_basic'] . '>Dashboard/Cancel</a>'; ?></div><?phpecho validation_errors('<p>');$messages = $this->ion_auth->messages();echo $messages;$errors = $this->ion_auth->errors();echo $errors;?></div>		</div>