<?php$order_id = $this->session->userdata('order_id');$user_id = $this->session->userdata('user_id');$role = $this->session->userdata('permission');if (!isset($role) || $role < 1) {    redirect(site_url('/order/login'));}?><img style="position:absolute; left:25px; top:5px;" src="<?php base_url(); ?>/images/mario.64.png" /><div class="ui-bar ui-bar-e"><h2><?php echo $category; ?> Choices</h2></div> <?phpif (isset($menu_items)) {    foreach ($menu_items->result() as $item) {	$enabled = $item->enabled;	if ($enabled == FALSE)	    continue;	$id = $item->id;	$name = $item->item_name;	$price = (float) $item->price;	$top_price = (float) $item->topping_price;	$show_top_price = (bool) $item->show_top_price;	$combo_top_prices = (bool) $item->combo_top_prices;	$desc = $item->description;	$table = $item->toppings_table;	echo '<div>';	echo '<form action="' . site_url('order/add_item') . '" method="post">';	echo '<input type="hidden" name="item_id" value="' . $id . '"/>';	echo '<input type="submit" name="submit" value="' . $name . '"/>';	echo '</form>';	//echo '<a href="'.site_url('order/add_item').'/'.$id.'"'.$this->config->config['link_menu_add'].'>'.$name.'</a>';		if ($show_top_price == TRUE && $combo_top_prices == FALSE) {	    echo '<div><p>' . $desc . '    $' . $price . '     (Toppings $' . $top_price . ')</p></div>';	} else {	    echo '<div><p>' . $desc . '    $' . $price . '</p></div>';	}	echo '</div>';    }}?><br/><div data-role="controlgroup">    <a href="<?php echo site_url("order/view_order") . '?id=' . $order_id; ?>" <?php echo $this->config->config['link_options_basic']; ?> rel="external">View Order</a>    <a href="<?php echo site_url("order/order_top"); ?>" <?php echo $this->config->config['link_options_basic']; ?> >Main Menu</a><?php if ($role > 1) echo '<a href="' . site_url("manager/view_order?id=") . $order_id . '"' . $this->config->config['link_options_basic'] . '>Dashboard</a>'; ?></div><?phpif ($order_id > 0) {    echo '<br/><p style="color: green;">Order Id: ' . $order_id . '</p>';} else {    echo '<br/><p style="color: red;">Order Id: ' . $order_id . '</p>';    echo '<p style="color: red;">Problem: Try refreshing your browser and logging in again.</p>';}?>