<?php$role = $this->session->userdata('permission');$user_loc = $this->session->userdata('location');?><div class="ui-bar ui-bar-e" style="text-align:center;"><h4 >Quick Order Login</h4></div><br/><form id="startOrderForm" action="<?php echo site_url('order/init_quick_order'); ?>" method="post" accept-charset="utf-8">     <div data-role="fieldcontain">		<label for="location">Location</label>	<select name="location" id="location" >	    <?php	    if (!isset($user_loc))		$luser_loc = 0;	    //($user_loc == 0) ? $selected = 'selected="yes"' : $selected="";	    if ($user_loc == 0)		echo '<option selected="yes" value="0">No Location Set</option>';	    foreach ($locations->result() as $loc) {		if ($luser_loc == $loc->id)		    $selected = 'selected="yes"';		echo '<option ' . $selected . ' value="' . $loc->id . '">' . $loc->location_name . '</option>';		$selected = '';	    }	    ?>				</select>    </div>    <div data-role="fieldcontain">		<label for="delivery">Delivery</label>	<input type="checkbox" id="delivery" name="delivery" value="1" checked="false"/>    </div>    <div data-role="fieldcontain">	<label for="name">Name</label>	<input type="text" name="name" id="name" value="<?php if (isset($name)) echo $name; ?>" autofocus />    </div>    <div data-role="fieldcontain">		<label for="phone">Phone Number</label>	<input type="tel" name="phone" id="phone" value="<?php if (isset($phone)) echo $phone; ?>"/>    </div>    <div data-role="fieldcontain">		<label for="carrier">Phone Carrier (Optional)</label>	<select name="carrier" id="carrier" >	    <option <?php if ((isset($carrier) && $carrier == 'no_message') || !isset($carrier)) echo 'selected="yes"'; ?> value="no_message">No Text Messages</option>	    <option <?php if (isset($carrier) && $carrier == 'vtext.com') echo 'selected="yes"'; ?> value="vtext.com">Verizon</option>	    <option <?php if (isset($carrier) && $carrier == 'txt.att.net') echo 'selected="yes"'; ?> value="txt.att.net">ATT</option>	    <option <?php if (isset($carrier) && $carrier == 'vmobl.com') echo 'selected="yes"'; ?> value="vmobl.com">Virgin</option>	    <option <?php if (isset($carrier) && $carrier == 'tmomail.net') echo 'selected="yes"'; ?> value="tmomail.net">TMobile</option>	    <option <?php if (isset($carrier) && $carrier == 'gocbw.com') echo 'selected="yes"'; ?> value="gocbw.com">Cincinnati Bell</option>	    <option <?php if (isset($carrier) && $carrier == 'sms.mycricket.com') echo 'selected="yes"'; ?> value="sms.mycricket.com">Cricket</option>	    <option <?php if (isset($carrier) && $carrier == 'txt.att.net') echo 'selected="yes"'; ?> value="txt.att.net">Tracfone</option>	    <option <?php if (isset($carrier) && $carrier == 'mmst5.tracfone.com') echo 'selected="yes"'; ?> value="mmst5.tracfone.com">Tracfone (Prepaid)</option>	    <option <?php if (isset($carrier) && $carrier == 'messaging.nextel.com') echo 'selected="yes"'; ?> value="messaging.nextel.com">Nextel</option>	</select>    </div>    <div class="ui-body ui-body-b">	<fieldset class="ui-grid-a">			    <input type="submit" <?php echo $this->config->config['submit_button_basic']; ?> name="submit" value="Go"/>								</fieldset>    </div>    <br/></form><?php//ajax this?echo validation_errors('<p class="error">');if (isset($problem)) {    echo '<p class="error">' . $problem . '</p>';}?><div data-role="controlgroup"><?phpif ($role > 1)    echo '<a href="' . site_url("manager/dashboard") . '"' . $this->config->config['link_options_basic'] . '>Dashboard</a>';?></div>