<script type="text/javascript">    // this identifies your website in the createToken call below    Stripe.setPublishableKey('pk_0F4XyesQTEdCv9aPtamIhsLSOWZ7c');  //test      function stripeResponseHandler(status, response) {                if (response.error) {                    // re-enable the submit button                    $('.submit-button').removeAttr("disabled");                    // show the errors on the form                    $(".payment-errors").html(response.error.message);                } else {                    var form$ = $("#payment-form");                    // token contains id, last4, and card type                    var token = response['id'];                    // insert the token into the form so it gets submitted to the server                    form$.append("<input type='hidden' name='stripeToken' value='" + token + "' />");                    // and submit                    form$.get(0).submit();                }   }            $(document).ready(function() {  $("#payment-form").submit(function(event) {    // disable the submit button to prevent repeated clicks    $('.submit-button').attr("disabled", "disabled");    Stripe.createToken({        number: $('.card-number').val(),        cvc: $('.card-cvc').val(),        exp_month: $('.card-expiry-month').val(),        exp_year: $('.card-expiry-year').val()    }, stripeResponseHandler);    // prevent the form from submitting with the default action    return false;  });});</script><img style="position:absolute; left:25px; top:5px;" src="<?php base_url(); ?>/images/mario.64.png" /><div class="ui-bar ui-bar-e"><h2>Pay Online</h2></div><form action="<?php echo site_url('order/process_payment') ?>" method="POST" id="payment-form">  <div data-role="fieldcontain">    <label>Card Number</label>    <input type="text" size="20" autocomplete="off" class="card-number"/>  </div> <div data-role="fieldcontain">    <label>CVC</label>    <input type="text" size="4" autocomplete="off" class="card-cvc"/>  </div>  <div data-role="fieldcontain">    <label>Expiration (MM/YYYY)</label>    <input type="text" size="2" class="card-expiry-month"/>    <span> / </span>    <input type="text" size="4" class="card-expiry-year"/>  </div>  <div class="ui-body ui-body-b">	<fieldset class="ui-grid-a">	    <button type="submit" class="submit-button">Submit Payment</button>	</fieldset>  </div>	    </form><br/><a href="<?php echo site_url(''); ?>" <?php echo $this->config->config['link_options_basic']; ?>>Pay In Person</a><?php$messages = $this->ion_auth->messages();echo $messages;$errors = $this->ion_auth->errors();echo $errors;?><?phpecho validation_errors('<p class="error">');if (isset($problem)) {    echo '<p class="error">' . $problem . '</p>';}?>