<?php$order_id = $this->session->userdata('order_id');$user_id = $this->session->userdata('user_id');$role = $this->session->userdata('permission');if (!isset($role) || $role < 1) {    redirect(site_url('/order/login'));}$std_top = $this->session->userdata('std_toppings');if ($std_top < 1)    unset($std_top);$this->session->unset_userdata('current_topping_name');$this->session->unset_userdata('current_topping_price');?><?phpif ($toppings_table == 'menu_pizza_toppings')    $type = 'Pizza';if ($toppings_table == 'menu_sand_toppings')    $type = 'Sandwich';if ($toppings_table == "none")    $type = "";$item_name = $this->session->userdata('current_item_name');if (isset($std_top))    $item_name = $item_name . ' (Menu Edit)';?><img style="position:absolute; left:25px; top:5px;" src="<?php base_url(); ?>/images/mario.64.png" /><div class="ui-bar ui-bar-e"><h2><?php echo $type; ?> Toppings  >>  <?php echo $item_name; ?></h2></div> <?phpif ($topping_categories != "none") {    foreach ($topping_categories->result() as $cat) {	$display_name = ucfirst($cat->category);	$name = $cat->category;	echo '<a href="' . site_url('order/show_toppings') . '/' . $name . '/' . $toppings_table . '" ' . $this->config->config['link_menu_basic'] . ' >' . $display_name . '</a>';    }}echo '<a href="' . site_url('order/special_instruction') . '/' . $toppings_table . '" ' . $this->config->config['link_menu_basic'] . '>Special Instruction</a>';?><br/>	<div data-role="controlgroup"><?phpif (!isset($std_top))    echo '<a href="' . site_url("order/view_order") . '?id=' . $order_id . '"' . $this->config->config['link_options_basic'] . ' rel="external" >View Order</a>';if (!isset($std_top))    echo '<a href="' . site_url("order/order_top") . '"' . $this->config->config['link_options_basic'] . '>Main Menu</a>';if (isset($std_top))    echo '<a href="' . site_url("manager/bounce_to_item") . '"' . $this->config->config['link_options_basic'] . '>Back To Item</a>';if ($role > 1)    echo '<a href="' . site_url("manager/dashboard") . '"' . $this->config->config['link_options_basic'] . '>Dashboard</a>';?></div><?phpif ($order_id > 0) {    echo '<br/><p style="color: green;">Order Id: ' . $order_id . '</p>';} else {    if (!isset($std_top)) {	echo '<br/><p style="color: red;">Order Id: ' . $order_id . '</p>';	echo '<p style="color: red;">Problem: Try refreshing your browser and logging in again.</p>';    }}?>