<?php$order_id = $this->session->userdata('order_id');$user_id = $this->session->userdata('user_id');$role = $this->session->userdata('permission');if (!isset($role) || $role < 1) {    redirect(site_url('/order/login'));}$email = $this->session->userdata('email_address');date_default_timezone_set($this->config->item('timezone'));if (!isset($delivery))    $delivery = FALSE;if ($delivery) {    $type = "DELIVERY";}else    $type = "PICKUP";?><?php$order_row = $order->row();if (!isset($order_id) || $order_id == "") {    $order_id = $order_row->id;    $this->session->set_userdata('order_id', $order_id);    $this->session->set_userdata('phone', $order_row->phone);    $this->session->set_userdata('name', $order_row->name);    $this->session->set_userdata('carrier', $order_row->carrier);}$role = $this->session->userdata('permission');$submitted = $order_row->submitted;$verified = $order_row->verified;$phone = $order_row->phone;$ready = $order_row->ready_done;$start_year = date("Y");if ($start_year == 12) {    $end_year = $start_year + 1;}else    $end_year = $start_year;?><script type="text/javascript">    $(document).ready(function () {    	$('#datetime').scroller({ preset: 'datetime', theme: 'android', minDate: new Date(now.getFullYear(), now.getMonth(), now.getDate()), mode: 'scroller', stepMinute: '10', endYear: <?php echo $end_year; ?>, startYear: <?php echo $start_year; ?>});    });    function getSchedule()    {	$.get('<?php echo site_url("order/get_schedule"); ?>',  function(result) {	    document.deliveryTimeForm.datetime.value = result;	});                }    function saveSchedule(val){	//$('#datetime').load('<?php echo site_url("order/save_schedule"); ?>?time='+encodeURIComponent(val));	$.get('<?php echo site_url("order/save_schedule"); ?>?time='+encodeURIComponent(val),  function(result) {	    document.deliveryTimeForm.datetime.value = result;	});                }    getSchedule();</script> <div class="ui-bar ui-bar-e" style="text-align:center;"><h4 >[Order#:<?php echo $order_id; ?>] <?php echo $phone; ?></h4></div><br/><?phpif ($open) {    if ($delivery) {	echo '<strong style="color: green;">Delivery Running</strong>  (Delivery Hours: ' . $hours . ')';    } else {	echo '<strong style="color: green;">Open</strong>  (Hours: ' . $hours . ')';    }} else {    if ($delivery) {	echo '<strong style="color: red">No Delivery.</strong>  (Delivery Hours: ' . $hours . ')';    } else {	echo '<strong style="color: red;">Closed.</strong>  (Hours: ' . $hours . ')';    }}?><form name="deliveryTimeForm">     <div data-role="fieldcontain">		<?php	if ($delivery) {	    echo '<label for="datetime">Scheduled Delivery Time:</label>';	} else {	    echo '<label for="datetime">Scheduled Pickup Time:</label>';	}	?>	<input type="text" name="datetime" id="datetime" class="mobiscroll" onload="getSchedule()" onchange="saveSchedule(this.value)" />    </div></form><table style="width: 90%;">    <?php    $total = 0.00;    if (isset($items)) {	echo '<tr style="text-align: left;"><th></th><th>Item/Topping</th><th>Half</th><th>Price</th><th><a href="' . site_url("order/order_top") . '" ' . $this->config->config['link_mini_icon'] . ' data-icon="plus" >Add Item</a></th></tr>';	foreach ($items->result() as $item) {	    $name = $item->name;	    $item_price = money_format('%.2n', $item->price);	    $item_id = $item->id;	    echo '<tr style="width:90%;"><td><a href="' . site_url("order/remove_item") . '?confirm=yes&id=' . $item_id . '&name=' . $name . '" data-rel="dialog" data-transition="pop" ' . $this->config->config['link_mini_icon'] . ' data-icon="minus">Remove</a></td><td style="color: blue;">' . $name . '</td><td></td><td>' . $item_price . '</td><td><a href="' . site_url('order/new_topping') . '/' . $item_id . '/' . $name . '"' . $this->config->config['link_mini_icon'] . ' data-icon="plus" >Add Topping</a></td></tr>';	    $total = $total + $item_price;	    foreach ($toppings->result() as $topping) {		$id = $topping->item_id;		$topping_id = $topping->id;		if ($item_id == $id) {		    $inst = $topping->comment;		    $price = money_format('%.2n', $topping->price);		    if ($price == 0)			$price = '';		    $half = $topping->half;		    if ($half == 'W' || $half == 'X')			$half = "";		    if ($half == 'L')			$half = "Left";		    if ($half == 'R')			$half = "Right";		    echo '<tr><td><a href="' . site_url("order/remove_topping?id=") . $topping_id . '" ' . $this->config->config['link_mini_icon'] . ' data-icon="delete" >Remove</a></td><td style="color: green;">  -  ' . $inst . '</td><td>' . $half . '</td><td>' . $price . '</td><td></td></tr>';		    $total = $total + $price;		}	    }	}	echo '<tr><td></td><td style="color: red;"><strong>' . $type . ' TOTAL</strong></td><td></td><td style="color:green;"><strong>$' . money_format('%.2n', $total) . '</strong></td><td></td></tr>';    }    ?></table><form id="promoForm" action="<?php echo site_url("order/apply_promo"); ?>" method="post" accept-charset="utf-8"><table>    <tr><td><input type="text" placeholder="Promo Code" name="promo_code" id="promo_code"/></td><td><input type="submit" <?php echo $this->config->config['submit_button_basic']; ?> name="submit" value="Apply"/></td></tr></table></form><br/>    <?php if ($role > 1) echo '<a href="' . site_url("manager/dashboard") . '"' . $this->config->config['link_options_back'] . '>Dashboard</a>'; ?><div data-role="controlgroup">    <?php if ($total > 0 && $submitted == FALSE) echo '<a href="' . site_url("order/submit_order") . '" ' . $this->config->config['link_options_basic'] . '>Submit Order</a>'; ?>    <a href="<?php echo site_url("order/order_top") . '/' . $order_id; ?>" <?php echo $this->config->config['link_options_basic']; ?> >Menu</a>    <a href="<?php echo site_url("order/order_address") . '/' . $order_id; ?>" <?php echo $this->config->config['link_options_basic']; ?> >Delivery Info</a>    <?php if ($role > 1 && $verified == FALSE && $submitted > 0) echo '<a href="' . site_url("manager/mark_order") . '?id=' . $order_id . '&field=verified' . ' "' . $this->config->config['link_options_basic'] . '>Mark as Verified</a>'; ?>    <?php if ($role > 1 && $verified == TRUE && $ready == FALSE) echo '<a href="' . site_url("manager/mark_order") . '?id=' . $order_id . '&field=ready' . '"' . $this->config->config['link_options_basic'] . '>Mark Complete</a>'; ?>    <?php if ($role > 1 && $ready == TRUE) echo '<a href="' . site_url("manager/mark_order") . '?id=' . $order_id . '&field=not_ready' . ' "' . $this->config->config['link_options_basic'] . '>Mark Incomplete</a>'; ?><?php if ($role > 1 && $submitted > 0) echo '<a href="' . site_url("manager/print_order") . '?id=' . $order_id . '" ' . $this->config->config['link_options_basic'] . '>Print Order</a>'; ?><?php if ($role > 1 && $delivery) echo '<a href="' . site_url("manager/send_map") . '" ' . $this->config->config['link_options_basic'] . '>Send Map</a>'; ?><?php if ($user_id > 0 && $role < 2) echo '<a href="' . site_url("order/name_order") . '" ' . $this->config->config['link_options_basic'] . '>Save Order</a>'; ?><?php if ($role > 2 && $submitted > 0) echo '<a href="' . site_url("manager/delete_order") . '?id=' . $order_id . '" ' . $this->config->config['link_options_basic'] . '>Discard Order</a>'; ?><?php if ($user_id > 0 && $role < 2) echo '<a href="' . site_url("order/profile") . '" ' . $this->config->config['link_options_basic'] . '>My Profile</a>'; ?>    <a href="<?php echo site_url("order/logout"); ?>" <?php echo $this->config->config['link_options_basic']; ?> data-icon="minus" >Cancel/Logout</a></div>